import {Flex,Center,Input,Button,} from '@chakra-ui/react';
import {useState} from "react"
import Map from './Map';
import axios from "axios"
import { useEffect } from 'react';
import {Currentweather} from "./Currentweather"

function Navbar(){
 const [city,setCity] = useState("");
 const [current,setCurrent] = useState([]);

const handlechange = ()=>{
  return  axios(
        {url:"http://api.weatherapi.com/v1/current.json?key=1ed8289ee0e54db88d1104939222906&q=Anantapur&aqi=no"}
        ).then((res)=>{
            setCurrent(res.data)
            console.log(current);
        })
        .catch((e)=>{
            return e
        })
};

useEffect(()=>{

},[current])



return (
    <div>
    <Flex p="10px" justifyContent={"center"} minH={"70px"} flexDirection={["column","row"]} gap={["10px","0px"]}>
  <Center px="10px">
  <Input 
  onKeyPress={({key})=> key === "Enter" ? handlechange : undefined}
  onChange = {(e)=>setCity({value : e.target.value})}
  borderRadius={"15px 0px 0px 15px"}
  width="200px"
  border={"2px grey solid"}
  placeholder='City'/>
  <Button colorScheme='blue'
   onClick={handlechange}
   borderRadius = {"0px 15px 15px 0px"}
   color={"white"}
  >Search</Button>
  </Center>
 
   <Center>
   <Button colorScheme='blue'>Button</Button>
   </Center>
</Flex>
  {city!=="" ?
  <div>
      <div>
     <Map  city={city}/>
     </div>
     <div></div>
 </div>
 : undefined
}
</div>
    )
}

export default Navbar;
