import axios from "axios";

function Currentweather ({e}){
    if(e.length===0) return 
    return (
     <div style={{justifyContent:"center",textAlign:"center",boxShadow:"rgba(0, 0, 0, 0.4) 0px 30px 90px",color:"blue.100"}}
     _hover={{zoom:"1.8"}}>
       <h1 style={{fontSize:"30px"}} >{e.location.name}</h1>
       <p style={{fontSize:"100px"}}>{e.current.temp_c}<sup>o</sup>C</p>
       <div style={{display:"flex",justifyContent:"center"}}>
           <h1 style={{fontSize:"30px"}}>{e.current.condition.text}</h1>
           <img src={`https:${e.current.condition.icon}` }/>
       </div>
     </div>
    )
}

export default Currentweather;
