import {Flex,Center,Input,Button,Icon} from '@chakra-ui/react';
import {useState} from "react"
import Loading from './Loading';
import {AppContext} from '../Context/AppContext';
import Map from './Map';
import axios from "axios"
import Details from './Details';
import { useEffect } from 'react';
import Current from './Current';
import {HiLocationMarker} from "react-icons/hi";
import { useContext } from 'react';
import { GetweatherByCity } from './Actions';

function Navbar(){


 const [city,setCity] = useState("");


 const value = useContext(AppContext);
 
const handlechange = ()=>{
     
};

// useEffect(()=>{
  
// },[current,state])

 const handleLocationData =  ()=>{
  //  navigator.geolocation.getCurrentPosition((position)=>{
  
  //  console.log(position.coords)
  //  return position;
  //  })
   console.log(city);
  GetweatherByCity(city);
}

return value[0].isLoading ? 
(
  <Loading/>
) : (
    <div>
    <Flex p="10px" justifyContent={"center"} minH={"70px"} flexDirection={["column","row"]} gap={["10px","0px"]}>
  <Center px="10px">
  <Input 
  onKeyPress={({key})=> key === "Enter" ? handlechange : undefined}
  onChange = {(e)=>setCity(e.target.value)}
  borderRadius={"15px 0px 0px 15px"}
  width="200px"
  border={"2px grey solid"}
  placeholder='City'/>
  <Button colorScheme='blue'
  bg={'#5e82f4'}
  _hover={{ 'bg': '5e82f4' }}
   onClick={handlechange}
   borderRadius = {"0px 15px 15px 0px"}
   color={"white"}
  >Search</Button>
  </Center>
 
   <Center>
   <Button colorScheme='blue'
    bg={'#5e82f4'}
    _hover={{ 'bg': '5e82f4' }}
    color={'white'}
    w={'100%'}
    borderRadius={'15px'}
    leftIcon={<Icon w={'30px'} h={'30px'} as={HiLocationMarker} />}
    onClick={handleLocationData}
   >Your Location</Button>
   </Center>
</Flex>
  {state ?
  <div>
    <div style={{display:"grid",gridTemplateColumns:"repeat(3,1fr)",gridGap:"30px",width:"90%",margin:"auto"}}>
     <Current e = {city} />
     <Details e={city}/>
     {/* <Currentdetails e={city} /> */}
     <Map  city={city}/>

     {/* <Data e={city} /> */}
     </div>
     <div></div>
 </div>
 : undefined
}
</div>
    )
}

export default Navbar;
