import {Flex,Center,Input,Button,} from '@chakra-ui/react';
import {useState} from "react"
import Data from './data';

function Navbar(){
 const [city,setCity] = useState("");

 const handlechange = (e)=>{
   setCity(e.target.value);
   console.log(city);
 }



return (
    <div>
    <Flex p="10px" justifyContent={"center"} minH={"70px"} flexDirection={["column","row"]} gap={["10px","0px"]}>
  <Center px="10px" width={"500px"}>
  <Input 
  onKeyPress={({key})=> key === "Enter" ? handlechange : undefined}
  onChange = {(e)=>setCity({value : e.target.value})}
  borderRadius={"15px 0px 0px 15px"}
  width="50px"
  border={"2px grey solid"}
  placeholder='City'/>
  <Button colorScheme='blue'
   onClick={handlechange}
   borderRadius = {"0px 15px 15px 0px"}
   color={"white"}
  >Search</Button>
  </Center>
 
   <Center>
   <Button colorScheme='blue'>Button</Button>
   </Center>
</Flex>
  {city}
  <div>
     <Data  city={city}/>
 </div>
</div>
    )
}

export default Navbar;
