 import {useEffect, useState} from "react";

import {Modal,ModalOverlay,ModalContent,ModalHeader,ModalFooter,ModalBody,ModalCloseButton,Button,useDisclosure,Lorem} from '@chakra-ui/react'


export default function Forecast(e) {
    const [data,setData] = useState([]);
    useEffect(()=>{
        setData(e.e);
    },[e])
   
 
    const { isOpen, onOpen, onClose } = useDisclosure()
    return (
   
         <div>
        {data.map((e,i)=>{
          console.log(i);
        
        return(
        <div  onClick={onOpen}>Open Modal</div>
        )
    })}

    {data.map((e)=>{

   
        <Modal  isOpen={isOpen} onClose={onClose}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Modal Title</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
               
            <div style={{justifyContent:"center",textAlign:"center",boxShadow:"rgba(0, 0, 0, 0.4) 0px 30px 90px",color:"blue.300",display:"flex"}}>
              <div style={{fontSize:"30px",justifyContent:"start"}}>
             
             <h1>Humidity</h1>
             <h1>Max_Wind</h1>
             <h1>Pressure</h1>
             <h1>Visibility</h1>
             <h1>Max_Temp</h1>
             <h1>Min_Temp</h1>

            </div>
           <div style={{fontSize:"30px",justifyContent:"start"}}>
             <h1>{e.day.avghumidity}%</h1>
             <h1>{e.day.maxwind_kph}Km/h</h1>
             <h1>{e.day.avgvis_km}</h1>
             <h1>{e.day.maxtemp_c}<sup>o</sup>C</h1>
             <h1>{e.day.mintemp_c}</h1>
             <h1></h1>
           
           </div>
         </div>
    
            </ModalBody>
          </ModalContent>
        </Modal>
         })}
        </div> 
     
    )
  }
