      url: 'https://community-open-weather-map.p.rapidapi.com/forecast/daily',
      params: {
       
         lat: `${latitude}`,
         lon: `${longitude}`,
         cnt: '10',
        units: 'metric'
      },
      headers: {
        'X-RapidAPI-Key': '12aa19a7d5mshac01930e68c38e9p122ad4jsnca3f38e9bed8',
        'X-RapidAPI-Host': 'community-open-weather-map.p.rapidapi.com'
      }
    };
    let forecast =  await axios.request(options)
    let weather = await axios(
      {url:`https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=&appid=fe3c983df64d3e2dd4227bf73441ae78`}
       )
  
     let weatherData = weather.data
