import {useEffect, useState} from "react";
import axios from "axios";
function Currentdetails ({e}){
  const [data,setData]=useState([]);

  useEffect(()=>{
    
      axios(
        {url:`http://api.weatherapi.com/v1/current.json?key=1ed8289ee0e54db88d1104939222906&q=${e}`}
        ).then((res)=>{
            setData(res.data)
            console.log(data.current);
        })
        .catch((err)=>{
            return e
        })
  },[e])
 
  if(data.length===0) return <div>...Loading</div>
  
 return (
    <div style={{justifyContent:"center",textAlign:"center",boxShadow:"rgba(0, 0, 0, 0.4) 0px 30px 90px",color:"blue.300",display:"flex"}}>
        <div style={{fontSize:"30px",justifyContent:"start"}}>
            <h1>Felt Temp</h1>
            <h1>Humidity</h1>
            <h1>Wind</h1>
            <h1>Visibility</h1>
            <h1>Temp</h1>
          
        </div>
        <div style={{fontSize:"30px",justifyContent:"start"}}>
            <h1>{data.current.feelslike_c}<sup>o</sup>C</h1>
            <h1>{data.current.humidity}%</h1>
            <h1>{data.current.wind_kph}Km/h</h1>
            <h1>{data.current.vis_km}Km</h1>
            <h1>{data.current.temp_c}<sup>o</sup>C</h1>
           
        </div>
    </div>
 )
}

export default Currentdetails;
