export default function Forecast(e) {
    const { isOpen, onOpen, onClose } = useDisclosure()
    return (
      <>
          {e.map((elem,i)=>{

          })}
        <div key="i" onClick={onOpen}>Open Modal</div>
  
        <Modal isOpen={isOpen} onClose={onClose}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Modal Title</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
               
            <div style={{justifyContent:"center",textAlign:"center",boxShadow:"rgba(0, 0, 0, 0.4) 0px 30px 90px",color:"blue.300",display:"flex"}}>
           <div style={{fontSize:"30px",justifyContent:"start"}}>
             
             <h1>Humidity</h1>
             <h1>Max_Wind</h1>
             <h1>Pressure</h1>
             <h1>Visibility</h1>
             <h1>Max_Temp</h1>
             <h1>Min_Temp</h1>
         
          
            </div>
           <div style={{fontSize:"30px",justifyContent:"start"}}>
             <h1>{elem.avghumidity}%</h1>
             <h1>{elem.maxwind_kph}Km/h</h1>
             <h1>{elem.avgvis_km}</h1>
             <h1>{elem.maxtemp_c}<sup>o</sup>C</h1>
             <h1>{elem.mintemp_c}</h1>
             <h1></h1>
           
           </div>
         </div>
     
            </ModalBody>
  
            <ModalFooter>
              <Button colorScheme='blue' mr={3} onClick={onClose}>
                Close
              </Button>
              <Button variant='ghost'>Secondary Action</Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
    
     
      </>
    )
  }
