// export default Forecast;



export default function Forecast(e) {
    const { isOpen, onOpen, onClose } = useDisclosure()
    return (
      <>
        <div onClick={onOpen}>Open Modal</div>
  
        <Modal isOpen={isOpen} onClose={onClose}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Modal Title</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
                {e.map((elem)=>{

               
    <div style={{justifyContent:"center",textAlign:"center",boxShadow:"rgba(0, 0, 0, 0.4) 0px 30px 90px",color:"blue.300",display:"flex"}}>
      <div style={{fontSize:"30px",justifyContent:"start"}}>
             <h1></h1>
             <h1>Humidity</h1>
             <h1>Wind</h1>
             <h1>Visibility</h1>
             <h1>Max_Temp</h1>
          
         </div>
         <div style={{fontSize:"30px",justifyContent:"start"}}>
             {/* <h1>{e.current.feelslike_c}<sup>o</sup>C</h1>
             <h1>{data.current.humidity}%</h1>
             <h1>{data.current.wind_kph}Km/h</h1>
             <h1>{data.current.vis_km}Km</h1>
             <h1>{data.current.temp_c}<sup>o</sup>C</h1> */}
           
         </div>
     </div>
      })}
            </ModalBody>
  
            <ModalFooter>
              <Button colorScheme='blue' mr={3} onClick={onClose}>
                Close
              </Button>
              <Button variant='ghost'>Secondary Action</Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
      </>
    )
  }
