import { WeathetAPI } from "./Api";
import {useEffect, useState} from "react";
import axios from "axios";
import Current from "./Current";
import Details from "./Details";
import Forecast from "./Forecast";


export function Data({e}){
    const [data,setData]=useState([]);

  useEffect(()=>{
    
      axios(
        {url:`http://api.weatherapi.com/v1/forecast.json?key=${WeathetAPI}&q=${e}&days=10`}
        ).then((res)=>{
            setData(res.data)
            console.log(data);
        })
        .catch((err)=>{
            return err
        })
  },[e])

  if(data.length===0) return 

  return (
      <div>
       <Current e={data}/>
       <Details e={data}/>
       <Forecast e={data.forecast.forecat} />
      </div>
  )
}
