import {useEffect, useState} from "react";
import axios from "axios";
function Details ({e}){
  const [data,setData]=useState([]);
  
  useEffect(()=>{
    
      axios(
        {url:`http://api.weatherapi.com/v1/current.json?key=1ed8289ee0e54db88d1104939222906&q=${e}`}
        ).then((res)=>{
            setData(res.data)
            console.log(data);
        })
        .catch((err)=>{
            return err
        })
  },[e])
 
  if(data.length===0) return <div>...Loading</div>
  
 return (
    <div className="Zoom" style={{justifyContent:"center",textAlign:"center",boxShadow:'0px 0px 30px 6px #E2E2E2',color:"blue.300",display:"flex"}}>
         <div style={{fontSize:"30px",justifyContent:"start"}}>
            <h1>Felt Temp</h1>
            <h1>Humidity</h1>
            <h1>Wind</h1>
            <h1>Visibility</h1>
            <h1>Max_Temp</h1>
            <h1>Min_Temp</h1>
          
        </div>
        <div style={{fontSize:"30px",justifyContent:"start"}}>
            <h1>{data.current.feelslike_c}<sup>o</sup>C</h1>
            <h1>{data.current.humidity}%</h1>
            <h1>{data.current.wind_kph}Km/h</h1>
            <h1>{data.current.vis_km}Km</h1>
            {/* <h1>{data.forecast.forecastday[0].day.maxtemp_c}<sup>o</sup>C</h1>
            <h1>{data.forecast.forecastday[0].day.mintemp_c}<sup>o</sup>C</h1> */}
           
        </div> 
    </div>
 )
}

export default Details;
