import {useState,useEffect} from "react"
import axios from "axios";

function Current ({e}){
   
const [data,setData] = useState([]);
    useEffect(()=>{
        axios(
            {url:`http://api.weatherapi.com/v1/current.json?key=1ed8289ee0e54db88d1104939222906&q=${e}`}
            ).then((res)=>{
                setData(res.data)
                console.log(data.location);
            })
            .catch((err)=>{
                return err
            })
    },[e])
    if(data.length===0) return <div>....Loading</div>;
    return (
    
     <div style={{justifyContent:"center",textAlign:"center",boxShadow:"rgba(0, 0, 0, 0.4) 0px 30px 90px",color:"blue.300", }}>
       <h1 style={{fontSize:"30px"}} >{data.location.name}</h1>
       <p style={{fontSize:"100px"}}>{data.current.temp_c}<sup>o</sup>C</p>
       <div style={{display:"flex",justifyContent:"center"}}>
           <h1 style={{fontSize:"30px"}}>{data.current.condition.text}</h1>
           <img src={`https:${data.current.condition.icon}` }/>
       </div>
     </div>
    )
}

export default Currentweather;
