// import {useEffect, useState} from "react";

import {Modal,ModalOverlay,ModalContent,ModalHeader,ModalFooter,ModalBody,ModalCloseButton,Button,useDisclosure,Lorem} from '@chakra-ui/react'








export default function Forecast(e) {
    console.log(typeof(e.e));
    const { isOpen, onOpen, onClose } = useDisclosure()
    return (
   
         <div>
         {e.map((elem)=>{
  console.log(elem);
         })}

        <div  onClick={onOpen}>Open Modal</div>
  
        <Modal  isOpen={isOpen} onClose={onClose}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Modal Title</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
               
            <div style={{justifyContent:"center",textAlign:"center",boxShadow:"rgba(0, 0, 0, 0.4) 0px 30px 90px",color:"blue.300",display:"flex"}}>
              <div style={{fontSize:"30px",justifyContent:"start"}}>
             
             <h1>Humidity</h1>
             <h1>Max_Wind</h1>
             <h1>Pressure</h1>
             <h1>Visibility</h1>
             <h1>Max_Temp</h1>
             <h1>Min_Temp</h1>

            </div>
           <div style={{fontSize:"30px",justifyContent:"start"}}>
             {/* <h1>{e[0].day.avghumidity}%</h1>
             <h1>{e[0].day.maxwind_kph}Km/h</h1>
             <h1>{e[0].day.avgvis_km}</h1>
             <h1>{e[0].day.maxtemp_c}<sup>o</sup>C</h1>
             <h1>{e[0].day.mintemp_c}</h1> */}
             <h1></h1>
           
           </div>
         </div>
     
            </ModalBody>
          </ModalContent>
        </Modal>
        </div> 
     
    )
  }
